import React from 'react'
import { animate, motion } from 'framer-motion'
import { Link } from 'react-router-dom';
import { AiOutlineEye, AiOutlineEyeInvisible } from 'react-icons/ai';
import { useState } from 'react';

function LeftSide() {
    const [formData, setFormData] = useState({
        email: '',
        password: '',
        captcha:''
      });

      const [showPassword, setShowPassword] = useState(false);

      function changeHandler(event) {
        setFormData((prevData) => ({
          ...prevData,
          [event.target.name]: event.target.value,
        }));
      }

  return (
    <motion.div className='w-1/2  bg-white'
        initial = {{
            y:"100vw"
        }}
        animate = {{
            y:"0"   
        }}
        transition={{
            duration: 2,

        }}
    >
        <form  className="flex flex-col items-center justify-center h-full bg-neutral-100">
                <div className="w-3/4  gap-y-7">
                    {/* Label for email address  */}
                    <label className="w-full">
                        <p className="text-2xl text-richblack-900 p-4 font-bold ">
                            Email Address<sup className="text-pink-200">*</sup>
                        </p>
                        <input
                            required
                            type="email"
                            value={formData.email}
                            onChange={changeHandler}
                            placeholder="Enter email address"
                            name="email"
                            className="bg-white rounded-[0.5rem] text-black w-full p-[12px]"
                        />
                    </label>

                    {/* Label for Password address  */}
                    <label className="w-full relative">
                        <p className="text-xl text-richblack-900 mb-4 font-bold  leading-[1.375rem]">
                            Password<sup className="text-pink-200">*</sup>
                        </p>
                        <input
                            required
                            type={showPassword ? 'text' : 'password'}
                            value={formData.password}
                            onChange={changeHandler}
                            placeholder="Enter Password"
                            name="password"
                            className="bg-white rounded-[0.5rem] text-black w-full p-[12px]"
                        />
                            <span className="absolute right-10 top-[48px] cursor-pointer" onClick={() => setShowPassword((prev) => !prev)}>
                                {showPassword ? (
                                <AiOutlineEyeInvisible fontSize={24} fill="black" />
                                ) : (
                                <AiOutlineEye fontSize={24} fill="black" />
                                )}
                            </span>

                        <div className=' max-w-max ml-auto mt-1'>
                        <a to="#" className="text-sm font-bold  underline  text-white hover:text-base hover:text-slate-300  ">
                            Forgot Password
                        </a>
                        </div>


                    </label>


                    <button className="bg-blue-600 rounded-[8px] font-medium text-richblack-900 w-full px-[12px] py-[8px] mt-6">
                        <div className='text-black font-bold text-xl'>
                            Sign In
                        </div>
                    </button>
                </div>
            </form>


    </motion.div>
  )
}

export default LeftSide
